import React, {Component} from 'react'
import '../Modal.css'

export default class Modal extends Component {

  state = {
    replyTo: "",
    fromName: "",
    toName: "",
    messageHtml: ""
    // cocktail: null
  }

  componentDidMount(){
    console.log(this.props.cocktail)
      this.setState({
        // cocktail: this.props.cocktail,
        messageHtml: `Cocktail: ${this.props.cocktail.name};
       Cocktail Description: ${this.props.cocktail.description};
       Cocktail Instructions: ${this.props.cocktail.instructions};
       Cocktail Ingredients: ${
         this.props.cocktail.proportions.map(proportion => {
          return proportion.ingredient_name
         })
       }`
      })
  }

  hideModal = () => {
    this.props.hideModal()
  }

  changeHandler = (e) => {
    this.setState({
      [e.target.name] : e.target.value
    })
  }

  render() {

      return(
        <div className = "bg-modal">
          <div className = "modal-content">
            <button onClick = {this.hideModal}>
            Back
            </button>
            <br/>

            <form onSubmit = {this.submitHandler}>

            BoozeR Buddy's Email: <input onChange = {this.changeHandler} type= "text" name= "replyTo" value = {this.state.replyTo} />
            <br/>
            Your Name: <input onChange = {this.changeHandler} type= "text" name= "fromName" value = {this.state.fromName} />
            <br/>
            BoozeR Buddy's Name: <input onChange = {this.changeHandler} type= "text" name= "toName" value = {this.state.toName} />
            <br/>
            Message: <textarea onChange = {this.changeHandler} type= "text" name= "messageHtml" value = {this.state.messageHtml} />
            <br/>
            <input type = 'submit' value = 'Send Cocktail'/>
            </form>

          </div>
        </div>
      )
    }


}
