  render() {
    return(
      <button className = "AddButton" >
        <span>
          {
            !this.props.displayForm?
              <div>
                New Cocktail
              </div>
            :
              <div>
                Hide Form
              </div>
          }
          <img className= "buttonIcon" alt = "pouring drink" src= "https://cdn2.iconfinder.com/data/icons/large-svg-icons/512/drink_toast_vector_symbol-512.png"/>
        </span>
      </button>
  )
  }

}
